<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Proveedores extends CI_Controller {	public function index()	{		$data['resaltar'] = 'proveedores';		$query = $this->db->get('proveedores');		$data['proveedores'] = $query->result_array();		$this->load->view('proveedores_view', $data);	}		public function nuevo(){		$this->load->view('proveedores_nuevo_view');	}		public function nuevoSave () {		$this->form_validation->set_message('required', 'Este campo es requerido');				$this->form_validation->set_rules('nombre', 'Nombre', 'required');		$this->form_validation->set_rules('direccion', 'Direccion', 'required');				if ($this->form_validation->run() == FALSE){			$data['resaltar'] = 'proveedores';			$this->load->view('proveedores_nuevo_view', $data);		}else{			$insertData = array(				'nombre'=>$this->input->post('nombre'),				'direccion'=>$this->input->post('direccion')			);			$this->db->insert('proveedores', $insertData);			redirect('proveedores');		}	}			public function editarSave ($id) {		$this->form_validation->set_message('required', 'Este campo es requerido');				$this->form_validation->set_rules('nombre', 'Nombre', 'required');		$this->form_validation->set_rules('direccion', 'Direccion', 'required');				if ($this->form_validation->run() == FALSE){			$data['resaltar'] = 'proveedores';			$this->load->view('proveedores_nuevo_view', $data);		}else{			$updateData = array(				'nombre'=>$this->input->post('nombre'),				'direccion'=>$this->input->post('direccion')			);			$this->db->where('id', $id);			$this->db->update('proveedores', $updateData);			redirect('proveedores');		}	}		public function editar($id){		$query = $this->db->get_where('proveedores', array('id'=>$id));		$ra = $query->result_array();		$data['nombre'] = $ra[0]['nombre']; 		$data['direccion'] = $ra[0]['Direccion'];		$this->load->view('proveedores_edit_view', $data);	}		public function eliminar($id){		$this->db->where('id', $id);		$this->db->delete('proveedores');		redirect('proveedores');	}}